import axios from "axios";
import React from "react";
import { useForm } from "react-hook-form";
import useAuth from "../../hooks/useAuth";

export default function Deposit() {
  const { user } = useAuth();
  const { register, reset, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data, "form");

    axios
      .post("https://limitless-lowlands-32082.herokuapp.com/questions", data)
      .then((res) => {
        if (res.data.insertedId) {
          reset();
        }
      });
  };
  return (
    <div className="mt-4">
      <form
        className="w-full max-w-lg flex flex-col justify-center text-center  ml-auto mr-auto"
        onSubmit={handleSubmit(onSubmit)}
      >
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          for="grid-first-name"
        >
          Name
        </label>
        <input
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("name")}
          defaultValue={user.displayName}
          placeholder="enter your name"
          required
        />
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Email
        </label>
        <input
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          defaultValue={user.email}
          {...register("email")}
          required
        />
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Write your question here
        </label>
        <textarea
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("question")}
          required
          placeholder="enter your text here"
          rows="6"
        />
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Gender
        </label>
        <select
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("gender")}
          required
        >
          <option value="male">male</option>
          <option value="female">female</option>
          <option value="other">other</option>
        </select>
        <input
          className="py-2 rounded mt-4 service-btn text-white"
          type="submit"
        />
      </form>
    </div>
  );
}
