import axios from "axios";
import React from "react";
import { useForm } from "react-hook-form";
import { toast, ToastContainer } from "react-toastify";
import useAuth from "../../hooks/useAuth";
import Payment from "../../Pages/Payment/Payment";
import "react-toastify/dist/ReactToastify.css";

export default function Deposit() {
  const { user, userInfo } = useAuth();
  console.log(user, userInfo, "user");
  const { register, reset, handleSubmit } = useForm();
  const onSubmit = (data) => {
    console.log(data, "form");

    data.image = userInfo?.image;
    data.date = new Date();

    axios.post("http://localhost:5000/deposit", data).then((res) => {
      if (res.data.insertedId) {
        reset();
        toast.success("Wow so easy!");
      }
    });
  };
  return (
    <div className="mt-4">
      <form
        className="w-full max-w-lg flex flex-col justify-center text-center  ml-auto mr-auto"
        onSubmit={handleSubmit(onSubmit)}
      >
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          for="grid-first-name"
        >
          Payment Type
        </label>
        <select
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("paymentType")}
          required
        >
          <option value="bkash">Bkash</option>
          <option value="rocket">Rocket</option>
          <option value="nagad">Nagad</option>
          <option value="cash">Cash</option>
        </select>
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Transaction ID
        </label>
        <input
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("transactionID")}
          required
        />
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Amount
        </label>
        <input
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("depositAmount")}
          required
        />
        <label
          className="block uppercase tracking-wide text-gray-700 text-xs font-bold mb-2 text-left ml-1 "
          htmlFor="name"
        >
          Comment
        </label>
        <textarea
          className="appearance-none block w-full bg-gray-200 text-gray-700 border border-red-500 rounded py-3 px-4 mb-3 leading-tight focus:outline-none focus:bg-white"
          {...register("comment")}
        ></textarea>
        <input
          className="py-2 rounded mt-4 service-btn text-white"
          type="submit"
        />
      </form>

      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />
    </div>
  );
}
